mapper.select:
SELECT MEMBER_ID, NAME, PHONE_NUM, REG_DT, PASSWORD
FROM MEMBER
LIMIT #{pagination.limitStart}, #{recordSize}

SELECT MEMBER_ID, NAME, PHONE_NUM, REG_DT, PASSWORD
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_ID, NAME, PHONE_NUM, REG_DT, PASSWORD
FROM MEMBER
WHERE NAME = #{name}

mapper.update:
UPDATE MEMBER
SET NAME=COALESCE(#{name},NAME), PHONE_NUM=COALESCE(#{phoneNum},PHONE_NUM)
WHERE MEMBER_ID = #{memberId}
