mapper.select:
SELECT member_id, name, phone_num, reg_dt
FROM member
where member_id = #{memberId}

SELECT member_id, name, phone_num, reg_dt
FROM member
where name = #{name}

mapper.update:
UPDATE MEMBER
SET  name=coalesce(#{name},name), phone_num=coalesce(#{phoneNum},phone_num)
where member_id = #{memberId}

mapper.delete:
DELETE MEMBER
FROM member
where member_id = #{memberId}
